$(function(){var a=$("#width"),n=$("#height"),e=$("#calculate"),i=$("#perimeter"),c=$("#area");e.click(function(){var e=a.val(),r=n.val(),t=new Rectangle(e,r);i.val(t.perimeter()),c.val(t.area())})}),exports.Rectangle=function(e,r){var t=Number(e),a=Number(r);this.area=function(){return t*a},this.perimeter=function(){return 2*(t+a)}};